import React,{useState} from 'react';
import "./Feed.css";
import StoryReel from './StoryReel';
import MessageSender from './MessageSender';
import Post from './Post';
import db from './firebase';
function Feed() {
    const[posts,setPosts]=useState([]);

    useEffect(()=>{
        db.collection("posts").onSnapshot((snapshot)=>
        setPosts(snapshots.docs.map((doc)=>({id:doc.id, data:doc.data()})))
    );
    },[]);
    return (
        <div className="Feed">
            <StoryReel />
             <MessageSender />
             <Post 
             profilePic="https://manojnath.tech/3.jpg"
             message="Test done"
             timestamp="timestap"
             username="Manoj"
             image="https://media.istockphoto.com/photos/first-person-point-of-view-of-a-woman-paddling-on-a-stand-up-paddle-picture-id1288844330?b=1&k=20&m=1288844330&s=170667a&w=0&h=nckXG0H5kPDbgDpC8iTObsiqG7Jwt6CeLuJ2WxdOTp4="
             
             />
             <Post 
             profilePic="https://manojnath.tech/3.jpg"
             message="Test done"
             timestamp="timestap"
             username="Manoj"
             
             />
             <Post />
        </div>
    );
};


export default Feed;
